import './singlepost.css';
import singlePost from '../../assets/singlepost.jpg';
import { useLocation } from 'react-router-dom';
import { useEffect } from 'react';

export default function SinglePost() {
	const location = useLocation();
	const path = location.pathname.split('/')[2];

	useEffect(() => {
		const getPost = async () => {
			const response = await fetch(`posts/${path}`);
			const data = await response.json();
			console.log(data);
		};
		getPost();
	}, [path]);

	return (
		<div className="singlePost">
			<div className="singlePostWrapper">
				<img className="singlePostImg" src={singlePost} alt="" />
			</div>
			<h1 className="singlePostTitle">
				Post Titile
				<div className="singlePostEdit">
					<i className="singlePostIcon fa-solid fa-pen-to-square"></i>
					<i className="singlePostIcon fa-solid fa-trash-can"></i>
				</div>
			</h1>
			<div className="singlePostInfo">
				<span className="singlePostAuthor">
					Author: <b> Rajesh </b>
				</span>
				<span className="singlePostDate">one hour ago</span>
			</div>
			<p className="singlePostDesc">
				Lorem ipsum dolor sit amet, consectetur adipisicing elit. Sed possimus
				odio dolores, expedita deserunt voluptatibus unde voluptate eius
				voluptas mollitia dolor ex non debitis molestiae sequi necessitatibus
				aliquid! Culpa, laudantium?
			</p>
		</div>
	);
}
